import { useState } from "react";
import {Mail, Phone, MapPin, Send} from "lucide-react";


const contactInformation = [
    {
        icon: Mail,
        title: "Email",
        value: "abdullasalem983@outlook.com",
        link: "mailto:abdullasalem983@outlook.com"
    },
    {
        icon: Phone,
        title: "Phone Number",
        value: "613-890-5976",
        link: "tel:613-890-5976"
    },
    {
        icon: MapPin,
        title: "Address",
        value: "1657 Frenette Street, Ottawa, Ontario",
        link: null
    },
];


function Contact() {
const [formInformation, setFormInformation] = useState ({
    name: "",
    email: "",
    message: ""
});

const [status, setStatus] = useState("");

function handleChange(e) {
    setFormInformation({
        ...formInformation,
        [e.target.name]: e.target.value
    });
}

async function handleSubmit(e) {
    e.preventDefault();
    try {
        const response = await fetch("http://localhost:5000/api/contact", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(formInformation),
        });
        const data = await response.json();

        if (data.ok) {
            setStatus("Message sent!");
            setFormInformation({name: "", email: "", message: ""});
        } else {
            setStatus("Failed to send. Please try again.");
        }
    } catch (error) {
        setStatus("Network error. Please try again.");
    }
}

return (
    <section id="contact" className="contact-section">
        <div className="contact-container">
            <h2 className="contact-maintitle">
                Contact Me
            </h2>
            <div className="contact-content">
                <div className="contact-info">
                    <h3 className="contact-subtitle">Get In Touch</h3>
                    <p className="contact-paragraph">
                        If interested in hiring me or want to collaborate on projects please contact me either by email or phone.
                    </p>
                    <div className="contact-information">
                        {contactInformation.map((info, index) => {
                            const Icon = info.icon;
                            return (
                                <div key={index} className="contact-item">
                                    <div className="contact-icon">
                                        <Icon size={24}/>
                                        </div>
                                        <div className="contact-text">
                                            <h4 className="contact-label">
                                                {info.title}
                                            </h4>
                                            {info.link ? (
                                                <a href={info.link} className="contact-link">
                                                    {info.value}
                                                </a>
                                            ) : (
                                                <span className="contact-value">{info.value}</span>
                                            )}
                                            </div>
                                        </div>
                                      )
                                    })}
                                 </div>
                              </div>
                              <form onSubmit={handleSubmit} className="contact-form">
                                <div className="form-submit">
                                    <label htmlFor="name" className="form-label">Name</label>
                                    <input
                                      type="text"
                                      id="name"
                                      name="name"
                                      value={formInformation.name}
                                      onChange={handleChange}
                                      required
                                      className="form-input"
                                      placeholder="Enter full name"
                                      />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="email" className="form-label">Email</label>
                                    <input
                                      type="email"
                                      id="email"
                                      name="email"
                                      value={formInformation.email}
                                      onChange={handleChange}
                                      required
                                      className="form-input"
                                      placeholder="Enter your email address"
                                      />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="message" className="form-label">Message</label>
                                    <textarea
                                      id="message"
                                      name="message"
                                      value={formInformation.message}
                                      onChange={handleChange}
                                      required
                                      className="form-textarea"
                                      placeholder="Send me a message"
                                      rows="5"
                                      />
                                </div>

                                <button type="submit" className="submit-btn">
                                    <Send size={25}/>
                                    Send
                                </button>

                                {status && (
                                    <p className={`status-message ${status.includes}`}>{status}</p>
                                )}
                             </form>
                        </div>
                    </div>
                </section>
              );
            };
            
            export default Contact;
