import { useState, useEffect } from "react" // it is used for managing state and side effects
import {Link} from "react-scroll" // used for smooth scrolling navigation 

function Navbar() {
    const [navActive, setNavActive] = useState
    (false); // state to manage the active state of the navbar

    const toggleNav = () => {
        setNavActive(!navActive) // toggles the active state of the navbar
    }

    const closeMenu = () => { // function to close the menu
        setNavActive(false) // sets the active state to false
    }

    useEffect(() => { // to handle window resize
        const handleResize = () => { // function for window resize
            if (window.innerWidth <= 500) { // if it is less than or equal to 500px
                closeMenu(); // then closes the menu
            }
        };

        window.addEventListener("resize", handleResize); // adds event listener for window resize
        
        return () => { // function to remove the event listener
            window.removeEventListener("resize", handleResize);
        };
    }, []); /// empty array means this effect runs once

    useEffect(() => {
        if (window.innerWidth <= 1200) {
            closeMenu();
        }
    }, []);

    return (
        <nav className={`navbar ${navActive ? "active" : ""}`}>
            <div>
                <img src="./img/logo.svg" alt="Logoipsum" />
            </div>
            <a className={`nav__hamburger ${navActive ? "active" : ""} `} onClick={toggleNav}>
                <span className="nav__hamburger__line"></span>
                <span className="nav__hamburger__line"></span>
                <span className="nav__hamburger__line"></span>
            </a>
            <div className={`navbar--items ${navActive ? "active" : ""}`}>
                <ul>
                    <li>
                        <Link
                         onClick={closeMenu}
                         activeClass="navbar--active-content"
                         spy={true}
                         smooth={true}
                         offset={-70}
                         duration={500}
                         to="heroSection"
                         className="navbar--content">
                            Home
                         </Link>
                         </li>
                        
                    <li>
                         <Link
                         onClick={closeMenu}
                         activeClass="navbar--active-content"
                         spy={true}
                         smooth={true}
                         offset={-70}
                         duration={500}
                         to="Portfolio"
                         className="navbar--content">
                            Portfolio
                         </Link>
                         </li>

                         <Link
                         onClick={closeMenu}
                         activeClass="navbar--active-content"
                         spy={true}
                         smooth={true}
                         offset={-70}
                         duration={500}
                         to="AboutMe"
                         className="navbar--content">
                            About Me
                         </Link>
                    
                </ul>
            </div>
        </nav>
    );
}

export default Navbar; // exported it so it can be used in other files
